import React from "react";
import { useEffect } from "react";
import { useReducer } from "react";

interface AuthState {
    validando: boolean,
    token: string | null,
    username: string,
    nombre: string,
}

const initialState: AuthState = {
    validando: true,
    token: null,
    username: '',
    nombre: ''
}

type AuthAction =
    | { type: 'logout'}
    | {type: 'login', payload: LoginPayLoad};
    const authReducer = (state: AuthState, action: AuthAction): AuthState => {
        switch (action.type) {
            case 'logout':
                return {
                    validando: false,
                    token: null,
                    username: '',
                    nombre: ''
                }
            case 'login':
                const {nombre, username} = action.payload;
                return {
                    validando: false,
                    token: 'MITOKEN1234567890',
                    //username: action.payload.username,
                    //nombre: action.payload.nombre,
                    username,
                    nombre,
                }
                break;
            default:
                return state;
                break;
        }
    }

type LoginPayLoad = {
    username: string,
    nombre: string
}


export const Login = () => {
    //const [{validando, token}, dispatch] = useReducer(authReducer, initialState)
    const [state, dispatch] = useReducer(authReducer, initialState)

    useEffect(() => {
        setTimeout(() => {
          dispatch({type:'logout'}) 
        }, 2500);
    }, []);

    dispatch({
        type: 'login',
        payload: {
            username: 'Dr.FIC',
            nombre: 'Frank'
        }
    })

    //Si se agregara una dependencia 
    if (state.validando) {
        return (
        <>
            <h3>Login</h3>
                <div className="ficAlertInfo_Div">
                    Validando Informaci√≥n...
                </div>   
        </>
        )
    }
    return (
        <>
            <h3>Login</h3>
            {
            (  state.token  )
                ? 
                (
                    <div className="ficAlertSuccess_Div">
                        Autenticado como: { state.nombre }
                    </div>
                )
                : 
                (   
                    <div className="ficAlertDanger_Div">
                        No Autenticado...
                    </div>
                )
            }
            {
                ( state.token )
                ? ( 
                    <button
                        className="ficLogout_Button"
                    >
                        Logout   
                    </button> 
                )
                : (   
                    <button
                        className="ficLogout_Button"
                    >
                        Login   
                    </button>   
                )
            }           
        </>
    )
}
